mapper.select:
SELECT member_idx, user_id, pwd, name, email, phone, reg_date, fail_cnt, last_login_date, member_state
FROM tbl_member
WHERE user_id = #{user_id}

mapper.update:
UPDATE tbl_member
SET fail_cnt = fail_cnt + 1
WHERE user_id = #{user_id}

UPDATE tbl_member
SET fail_cnt = 0, last_login_date = NOW()
WHERE user_id = #{user_id}
